mapper.select:
select aca_no, aca_name, aca_addr, aca_main_pic
from (select row_number() over(order by aca_no desc) as rnum, aca_no, aca_name, aca_addr, aca_main_pic from ACADEMY where upper (aca_addr) LIKE '%'||upper(#{ADDRESS})||'%')
where  rnum between #{STARTROWNUMBER}  and #{ENDROWNUMBER} order by aca_no desc

select cur_no, cur_name, cur_content, cur_main_pic
from (select row_number() over(order by cur_no desc) as rnum, cur_no, cur_name, cur_content, cur_main_pic from curriculum where upper (cur_name) LIKE '%'||upper(#{curName})||'%')
where rnum between #{STARTROWNUMBER}  and #{ENDROWNUMBER} order by cur_no desc
